mapper.select:
select oid as id , order_id as orderId, customer_id as customerId, invoice_type as invoiceType, invoice_title as invoiceTitle, invoice_content as invoiceContent, state, rece_province as receProvince, rece_city as receCity, rece_county as receCounty, rece_town as receTown, rece_address as receAddress, rece_full_address as receFullAddress, created_at as createdAt, updated_at as updatedAt, created_by as createdBy, updated_by as updatedBy, remark, delete_flag as deleteFlag from t_magicmall_invoice
1=1
and order_id=#{orderId}
and created_at between #{beginCreatedAt} and #{endCreatedAt}
order by #{page.orderBy}
